import React, {useState} from "react";
import "../css/btnsCitations.css";
import DisplayCitations from "./displayCitations.tsx";



function BtnsCitations() {


  const [signalMyCitations, setSignalMyCitation] = useState(false);
  const [signalKaamelottCitations, setSignalKaamelottCitation] = useState(false);


  const handleSignalMyCitation = () => {
    setSignalMyCitation(true);
  };

  const handleKaamelottCitations = () => {
    setSignalKaamelottCitation(true);
  };



  return (

    <div>
      <DisplayCitations 

detectSignalKaamelottCitation={signalKaamelottCitations}
              
              detectSignalMyCitation={signalMyCitations}
              resetSignal={() => setSignalMyCitation(false)}
      />


    
    <div className="container_btns">
      <div className="title_btns">
        <h2>Afficher une autre citation</h2>
      </div>

      <div className="btns">
        <button className="myCitations"onClick={handleSignalMyCitation} >
          <div className="btn">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="15"
              height="15" 
              viewBox="0 0 20 20"
            >
              <path
                fill="#6303ac"
                d="M10 4.4C3.439 4.4 0 9.232 0 10c0 .766 3.439 5.6 10 5.6c6.56 0 10-4.834 10-5.6c0-.768-3.44-5.6-10-5.6zm0 9.907c-2.455 0-4.445-1.928-4.445-4.307c0-2.379 1.99-4.309 4.445-4.309s4.444 1.93 4.444 4.309c0 2.379-1.989 4.307-4.444 4.307zM10 10c-.407-.447.663-2.154 0-2.154c-1.228 0-2.223.965-2.223 2.154s.995 2.154 2.223 2.154c1.227 0 2.223-.965 2.223-2.154c0-.547-1.877.379-2.223 0z"
              />
            </svg>
         
            Parmi mes citations

          
          </div>
        </button>

        <button className="KaamelottCitations" onClick={handleKaamelottCitations}>
          <div className="btn">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="15"
              height="15"
              viewBox="0 0 20 20"
            >
              <path
                fill="#6303ac"
                d="M10 4.4C3.439 4.4 0 9.232 0 10c0 .766 3.439 5.6 10 5.6c6.56 0 10-4.834 10-5.6c0-.768-3.44-5.6-10-5.6zm0 9.907c-2.455 0-4.445-1.928-4.445-4.307c0-2.379 1.99-4.309 4.445-4.309s4.444 1.93 4.444 4.309c0 2.379-1.989 4.307-4.444 4.307zM10 10c-.407-.447.663-2.154 0-2.154c-1.228 0-2.223.965-2.223 2.154s.995 2.154 2.223 2.154c1.227 0 2.223-.965 2.223-2.154c0-.547-1.877.379-2.223 0z"
              />
            </svg>
            Parmi les citations de Kaamelott
          </div>
        </button>
      </div>

    </div>
    <div className="line"></div>

    </div>
  );
}

export default BtnsCitations;
