import React, { useEffect, useState } from 'react';
import '../css/displayCitations.css';
import axios from 'axios';
import { toast } from 'react-toastify';


function DisplayCitations( {detectSignalKaamelottCitation,resetKaamelottSignal, detectSignalMyCitation, resetSignal }) {
  const [citation, setcitation] = useState('');
  const [personnage, setpersonnage] = useState('');
  const [episode, setepisode] = useState('');
  const [saison, setsaison] = useState('');
  const [auteur, setauteur] = useState('');
  const [acteur, setacteur] = useState('');
  const [checkEmptyRandom, setcheckEmptyRandom] = useState(10);

  useEffect(() => {
    if (detectSignalMyCitation) {
      getRandomCitation();
      resetSignal();
    }
  }, [detectSignalMyCitation]);

  useEffect(() => {
    if (detectSignalKaamelottCitation) {
      //getRandomKaamelottCitation();
      resetKaamelottSignal();
    }
  }, [detectSignalKaamelottCitation]);


  
// we can't call the kaamelott api from the front because both server are not in the same domaine (error :: cros) 
//solution ? call the kaamelott from the backend and then call the /api/random in th front :)

  const getRandomKaamelottCitation = async () => {

  const apiUrl = "http://localhost:3000/api/random"; 

axios.get(apiUrl)
  .then(response => {

    setcitation(response.data.citation.citation) 
    setpersonnage(response.data.citation.infos.personnage);
    setepisode(response.data.citation.infos.episode);
    setacteur(response.data.citation.acteur);
    setauteur(response.data.citation.infos.auteur);
    setsaison(response.data.citation.infos.saison);
    setcheckEmptyRandom(0);

    
  })
  .catch(error => {
    console.log("Erreur lors de l'appel à l'API via votre serveur backend.", error);
  });

  };
  
  const addfavoriteCitation = async () =>{
    try{
      const body = {
        citation: citation,
        auteur: auteur,
        acteur: acteur,
        personnage: personnage,
        saison: saison,
        episode: episode,
      }
      const response = await axios.post('http://localhost:3000/create_Citation',body);

      if(response.status === 20){
        toast.success("Citation enregistrer avec suucèes");
      }
      
    }catch(err){

      toast.error("une erreur à été detecter lors d'enregistrement de la citation ");
      console.log('erreur', err)

    }

  };

  const getRandomCitation = async () => {
    try {

      const response = await axios.get('http://localhost:3000/fetchRandomCitation');

      const { data, status } = response;
      if (status === 200) {
        setcheckEmptyRandom(0);

        const { citation, personnage, episode } = data;
        setcitation(citation);
        setpersonnage(personnage);
        setepisode(episode);
      } else if(status === 201){
        setcheckEmptyRandom(1);

        console.log(checkEmptyRandom)
      } else{
        throw new Error('Erreur lors de la récupération de la citation aléatoire.');
      }
    } catch (err) {
      console.error(err);
    }
  };

return (
  <div className='container_citations'>
    <div className='title'>
      <h1>Citations</h1>
    </div>
    <div className='container_border'>
      {checkEmptyRandom === 1 ?(
        <div className='mesCitationsDefault'>
           <p>Aucune citation disponible. Veuillez en créer une </p>

