import React, { useEffect, useState } from 'react';
import '../css/displayCitations.css';
import axios from 'axios';

function DisplayCitations( {detectSignalMyCitation, resetSignal }) {
  const [citation, setcitation] = useState('');
  const [personnage, setpersonnage] = useState('');
  const [episode, setepisode] = useState('');
  const [checkEmptyRandom, setcheckEmptyRandom] = useState(10);

  useEffect(() => {
    if (detectSignalMyCitation) {
      getRandomCitation();
      resetSignal();
    }
  }, [detectSignalMyCitation]);
  

  const getRandomCitation = async () => {
    try {

      const response = await axios.get('http://localhost:3000/fetchRandomCitation');

      const { data, status } = response;
      if (status === 200) {
        setcheckEmptyRandom(0);

        const { citation, personnage, episode } = data;
        setcitation(citation);
        setpersonnage(personnage);
        setepisode(episode);
      } else if(status === 201){
        setcheckEmptyRandom(1);

        console.log(checkEmptyRandom)
      } else{
        throw new Error('Erreur lors de la récupération de la citation aléatoire.');
      }
    } catch (err) {
      console.error(err);
    }
  };

return (
  <div className='container_citations'>
    <div className='title'>
      <h1>Citations</h1>
    </div>
    <div className='container_border'>
      {checkEmptyRandom === 1 ?(
        <p>Empty</p>
      ) : checkEmptyRandom === 10 ? (
        <div className='mesCitationsDefault'>
          <p>cliquer sur le bouton</p>
        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 48 48"><path fill="#ffe500" d="M4 21.5a20 20 0 1 0 40 0a20 20 0 1 0-40 0Z"/><path fill="#ebcb00" d="M24 1.5a20 20 0 1 0 20 20a20 20 0 0 0-20-20Zm0 37a18.25 18.25 0 1 1 18.25-18.25A18.25 18.25 0 0 1 24 38.5Z"/><path fill="#fff48c" d="M18 5.5a6 1.5 0 1 0 12 0a6 1.5 0 1 0-12 0Z"/><path fill="#45413c" d="M8 45.5a16 1.5 0 1 0 32 0a16 1.5 0 1 0-32 0Z" opacity=".15"/><path fill="none" stroke="#45413c" stroke-linecap="round" stroke-linejoin="round" d="M4 21.5a20 20 0 1 0 40 0a20 20 0 1 0-40 0Z"/><path fill="#ffaa54" d="M38.5 26.5c0 .83-1.12 1.5-2.5 1.5s-2.5-.67-2.5-1.5S34.62 25 36 25s2.5.67 2.5 1.5Zm-29 0c0 .83 1.12 1.5 2.5 1.5s2.5-.67 2.5-1.5S13.38 25 12 25s-2.5.67-2.5 1.5Z"/><path fill="#ffe500" d="M15.63 42.78a7.59 7.59 0 0 0 10.73 0l6.13-6.13A1.75 1.75 0 0 0 30 34.18l2.12-2.12a1.75 1.75 0 1 0-2.47-2.48l.71-.71a1.75 1.75 0 1 0-2.48-2.47l-1.41 1.41A1.75 1.75 0 1 0 24 25.34l-5.83 5.83l-.45-3.67a1.63 1.63 0 0 0-3 .25l-1.48 4.45a10.16 10.16 0 0 0 2.39 10.58Z"/><path fill="#ebcb00" d="m32.18 33.93l-5.82 5.81a7.57 7.57 0 0 1-10.73 0a10.18 10.18 0 0 1-2.86-5.66a10.14 10.14 0 0 0 2.86 8.7a7.59 7.59 0 0 0 10.73 0l6.13-6.13a1.75 1.75 0 0 0 0-2.47a1.79 1.79 0 0 0-.31-.25Z"/><path fill="none" stroke="#45413c" stroke-linecap="round" stroke-linejoin="round" d="m26.65 27.64l-4.24 4.24m7.43-2.48l-4.95 4.95m5.3-.35l-2.83 2.83M32.5 19.5s.17-2 2-2s2 2 2 2m-25 0s.17-2 2-2s2 2 2 2m.13 23.28a7.59 7.59 0 0 0 10.73 0l6.13-6.13A1.75 1.75 0 0 0 30 34.18l2.12-2.12a1.75 1.75 0 1 0-2.47-2.48l.71-.71a1.75 1.75 0 1 0-2.48-2.47l-1.41 1.41A1.75 1.75 0 1 0 24 25.34l-5.83 5.83l-.45-3.67a1.63 1.63 0 0 0-3 .25l-1.48 4.45a10.16 10.16 0 0 0 2.39 10.58Z"/></svg>
        </div>
        
      ) : (
        <>
          <div className='quote'>
            <h3> {citation} </h3>
          </div>
          <div className='arthur'>
            <p>{personnage} - '{episode}'</p>
          </div>
          <div className='container_favorite_btn'>
            <svg xmlns="http://www.w3.org/2000/svg" width="17" height="17" viewBox="0 0 24 24">
              <path fill="#6303ac" d="M12 .25a.75.75 0 0 1 .673.418l3.058 6.197l6.839.994a.75.75 0 0 1 .415 1.279l-4.948 4.823l1.168 6.811a.751.751 0 0 1-1.088.791L12 18.347l-6.117 3.216a.75.75 0 0 1-1.088-.79l1.168-6.812l-4.948-4.823a.75.75 0 0 1 .416-1.28l6.838-.993L11.328.668A.75.75 0 0 1 12 .25Zm0 2.445L9.44 7.882a.75.75 0 0 1-.565.41l-5.725.832l4.143 4.038a.748.748 0 0 1 .215.664l-.978 5.702l5.121-2.692a.75.75 0 0 1 .698 0l5.12 2.692l-.977-5.702a.748.748 0 0 1 .215-.664l4.143-4.038l-5.725-.831a.75.75 0 0 1-.565-.41L12 2.694Z"/>
            </svg>
            <a href="#" className='star_btn'>Mettre en favoris</a>
          </div>
        </>
      )}
    </div>
  </div>
);

  
  
}

export default DisplayCitations;
