import React, { useState, useEffect } from "react";
import "../css/edit.css";
import { toast } from 'react-toastify';
import axios from 'axios';

function Edit({ showEditPopup, idCitation, closeEditPopup }) {
  const [citations, setCitations] = useState({
    citation: '',
    auteur: '',
    acteur: '',
    personnage: '',
    saison: '',
    episode: '',
  });

  useEffect(() => {
    getAllCitation();
  }, []);

  const getAllCitation = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/fetchCitationById/${idCitation}`);
      const { data, status } = response;
      if (status === 200) {
        setCitations(data);
      } else {
        throw new Error('Erreur lors de la récupération des citations.');
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCitations((prevCitations) => ({
      ...prevCitations,
      [name]: value,
    }));
  };

  const confirm = async () => {
    try {
      const response = await axios.put(`http://localhost:3000/edit_Citation/${idCitation}`, citations);
      if (response.status === 201) {
        closeEditPopup();
        window.location.reload();
        toast.success("Modification effectuée avec succès");
      } else {
        toast.error("Erreur");
      }
    } catch (err) {
      console.log(err);
      toast.error("Erreur");
    }
  };

  return showEditPopup ? (
    <div id="app">
      <div className='popup'>
        <div className="profile-popup-content">
          <div className="title-profile">
            <h2>Modifier votre Citation</h2>
            <div className="line3"></div>
          </div>
          <div className="colonne-step">
            <div className="colonnne-step1-grid1">
              <div className="input-field">
                <label>Citation</label>
                <input
                  id="citation"
                  name="citation"
                  value={citations.citation}
                  onChange={handleInputChange}
                  type="text"
                  placeholder="Modifier votre citation"
                />
              </div>
              <div className="input-field">
                <label>Auteur</label>
                <input
                  id="auteur"
                  name="auteur"
                  value={citations.auteur}
                  onChange={handleInputChange}
                  type="text"
                  placeholder="Modifier le nom de l'auteur"
                />
              </div>
              <div className="input-field">
                <label>Episode</label>
                <input
                  id="episode"
                  name="episode"
                  value={citations.episode}
                  onChange={handleInputChange}
                  type="text"
                  placeholder="Quel épisode ?"
                />
              </div>
            </div>
            <div className="colonnne-step1-grid2">
              <div className="input-field">
                <label>Acteur</label>
                <input
                  id="acteur"
                  name="acteur"
                  value={citations.acteur}
                  onChange={handleInputChange}
                  type="text"
                  placeholder="Modifier le nom de l'acteur"
                />
              </div>
              <div className="input-field">
                <label>Personnage</label>
                <input
                  id="personnage"
                  name="personnage"
                  value={citations.personnage}
                  onChange={handleInputChange}
                  type="text"
                  placeholder="Modifier le nom du personnage"
                />
              </div>
              <div className="input-field">
                <label>Saison</label>
                <input
                  id="saison"
                  name="saison"
                  value={citations.saison}
                  onChange={handleInputChange}
                  type="text"
                  placeholder="Quelle saison ?"
                />
              </div>
            </div>
          </div>
          <div className="button-colonne margin">
            <div className="container_btn_invite">
              <a href="#" className="btn1" onClick={confirm}>
                Confirmer
              </a>
            </div>
            <div className="container_btn_invite">
              <a onClick={closeEditPopup} href="#" className="btn1">
                Annuler
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  ) : null;
}

export default Edit;
